import numpy as npimport matplotlib.pyplot as pltclass Reservior: #Element selected to be in the reservior - null initially reservior_element = None #Number of elements encountered so far reservior_size = 0 #Function: add element to reservior def add_element(self, x):  self.reservior_size = self.reservior_size + 1  #If the element is first element in the list, probability of selection is 1  if self.reservior_size == 1:   self.reservior_element = x  else:   #Choose between the new element and the current element in reservior   self.reservior_element = np.random.choice([self.reservior_element, x], 1, p=[1 - 1. / self.reservior_size, 1. / self.reservior_size])[0]trails = [1, 1000, 10000, 100000]max_num = 100for trial in trails:    #Array to record the number of times element was selected    counter_array = [0]*(max_num+1)    for i in range(trial):      #Creating a new reservior      current_reservior = Reservior()      #Adding element to reservior      for x in range(1,max_num+1):       current_reservior.add_element(x)      if trial == 1:       print("Element sampled in one run:",current_reservior.reservior_element)      else:       counter_array[current_reservior.reservior_element] +=1    #Plot graph for each trial: Element VS number of times element was selected    if trial != 1:        plt.plot(range(max_num), counter_array[1:max_num+1])        plt.xlabel('Element')        plt.ylabel('Number of times selected')        plt.title('Reservoir Sampling')        imagename = "figure_" + str(trial) + ".png"        plt.savefig(imagename)        plt.close()